		<?php echo apply_filters( 'milky_way_end_of_page', '' ); ?>		<div id="footer" class="footer">			<?php 				/*				 *	Aria labelledby adds relationship between the footer and it's heading. 				 */			?>			<footer role="contentinfo" aria-label="<?php _e( 'Footer Content', 'milky-way' ); ?>">				<?php echo apply_filters( 'milky_way_before_secondary_menu', '' ); ?>				<div class='secondary-menu'>					<?php 						/*						 *	Aria label differentiates between the secondary and primary nav regions.						 */					?>					<nav role="navigation" aria-label='<?php _e( 'Secondary Navigation', 'milky-way' ); ?>'>					<?php wp_nav_menu( array( 'theme_location'=>'secondary', 'depth'=>1 ) ); ?>					</nav>				</div>				<?php echo apply_filters( 'milky_way_after_secondary_menu', '' ); ?>				<?php echo apply_filters( 'milky_way_footer', "<p>&copy; " . get_bloginfo('name') . ", " . date_i18n( _x( 'Y', 'date format', 'milky-way' ) ) . "</p>" ); ?>				<?php echo apply_filters( 'milky_way_after_copyright', '' ); ?>			</footer>		</div>		<?php echo apply_filters( 'milky_way_after_footer', '' ); ?>	</div>	</div>		<?php wp_footer(); ?></body></html>